mapper.update:
update user
nickName=#{nickName}, avatar=#{avatar}, space_bg=#{spaceBg}, `description`=#{description}, update_time=sysdate()
where id = #{id}

mapper.select:
select id, nickName, name, password, avatar, space_bg spaceBg, `description`, create_time createTime from user u where 1=1

    and nickName=#{nickName}


    and name=#{name}
